import React, { useState, useRef, useEffect } from "react";
import {
  View,
  Text,
  Pressable,
  TextInput,
  Modal,
  StyleSheet,
  Image,
  TouchableOpacity,
  ActivityIndicator,
  ScrollView,
} from "react-native";
import { FontAwesome5, Ionicons } from "@expo/vector-icons";
import useForgot from "../../hooks/useResetPw";
import OTPTextView from "react-native-otp-textinput";
import Toast from "react-native-toast-message";
import useLogout from "../../hooks/useLogout";

const ForgotPassword = ({ navigation }) => {
  const [textEmail, setTextEmail] = useState(null);
  const [modalXacNhan, setModalXacNhan] = useState(false);
  const [modalOTP, setModalOTP] = useState(false);
  const [modelSuccess, setModalSuccess] = useState(false);
  const [modalCreatePw, setModalCreatePw] = useState(false);
  const [modalResetSuccess, setModalResetSuccess] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [isCounting, setIsCounting] = useState(false);
  const [newPassword, setNewPassword] = useState(null);
  const [newPassword2, setNewPassword2] = useState(null);
  const [buttonText, setButtonText] = useState("Hiện");
  const [showPassword, setShowPassword] = useState(false);
  const [otp, setOtp] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isHidden, setIsHidden] = useState(false);
  const {
    getOTP,
    showToastError,
    showToastSuccess,
    resetPassword,
    check_mail,
    handleOTP,
  } = useForgot();
  const [isPreSendCode, setIsPreSendCode] = useState(false);

  const logout = useLogout();

  useEffect(() => {
    navigation.setOptions({
      headerTitle: () => (
        <View style={{ flexDirection: "row", alignItems: "center" }}>
          <Text style={{ color: "white", fontSize: 20, fontWeight: "bold" }}>
            Lấy lại mật khẩu
          </Text>
        </View>
      ),
      headerStyle: {
        backgroundColor: "#0091FF",
        shadowColor: "#fff",
      },
      headerTintColor: "#fff",
      headerTitleStyle: {
        fontWeight: "bold",
        fontSize: 20,
      },
    });
  }, [navigation]);

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
    setButtonText(showPassword ? "Hiện" : "Ẩn");
  };

  // tiến hành gửi mã otp, nếu đã gửi sẽ hiển thị modal xác thực email
  const pressSendOTP = async (e) => {
    const systemOTP = await getOTP(textEmail);
    if (systemOTP) {
      setModalXacNhan(!modalXacNhan);
      setModalOTP(!modalOTP);
      setIsLoading(false);
      handlesendAuthCode();
    }
  };
  // tiến hành gửi lại mã otp, nếu đã gửi sẽ hiển thị modal cho nhập email
  const pressPreSendOTP = async (e) => {
    const systemOTP = await getOTP(textEmail);
    if (systemOTP) {
      isLoading(false);
      setTimeLeft(60);
      SendTime();
      setIsPreSendCode(false);
      setIsCounting(true);
    }
  };

  const handleNext = async () => {
    setIsLoading(true);
    if (!textEmail) {
      showToastError("Vui lòng nhập gmail");
      setIsLoading(false);
    } else if (!textEmail.trim().toLowerCase().endsWith("@gmail.com")) {
      showToastError("Email phải có định dạng @gmail.com");
      setIsLoading(false);
    } else {
      const sendEmail = await check_mail(textEmail);
      if (!sendEmail) {
        setIsLoading(false);
        return;
      }
      setIsLoading(false);
      setIsHidden(!isHidden);
      setModalXacNhan(!modalXacNhan);
      // setModalOTP(!modalOTP)
    }
  };

  const handleXacNhan = async () => {
    setIsLoading(true);
    pressSendOTP();
  };

  const handlesendAuthCode = async (e) => {
    setTimeLeft(60);
    SendTime();
    setIsPreSendCode(false);
  };

  const handleOTPChange = (enteredOtp) => {
    setOtp(enteredOtp);
  };
  // kiểm tra otp có đầy đủ không
  const handleVerifyOTP = () => {
    setIsLoading(true);
    if (otp.length === 6) {
      handleSubmit();
    } else {
      showToastError("Hãy nhập đủ mã xác thực");
      setIsLoading(false);
    }
  };

  // đếm thời gian giảm dần
  useEffect(() => {
    let timer;
    if (isCounting && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
    } else {
      setIsPreSendCode(true);
      clearInterval(timer);
      setIsCounting(false);
    }

    // Xóa interval khi component bị unmount
    return () => clearInterval(timer);
  }, [isCounting, timeLeft]);

  const SendTime = () => {
    setIsCounting(true);
  };

  useEffect(() => {
    if (timeLeft === 0) {
      return;
    }
  }, [timeLeft]);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? "0" + seconds : seconds}`;
  };

  const handleSubmit = async (e) => {
    setIsLoading(false);
    const response = await handleOTP(otp);
    if (response) {
      setModalOTP(!modalOTP);
      setModalSuccess(!modelSuccess);
    }
  };

  const handleResetPassword = async () => {
    setIsLoading(true);
    if (!newPassword) {
      showToastError("Vui lòng nhập mật khẩu mới");
      setIsLoading(false);
    } else if (!/^[A-Za-z\d@$!%*?&#]{6,}$/.test(newPassword)) {
      showToastError(
        "Mật khẩu phải có ít nhất 6 ký tự,chứa ít nhất 1 chữ,1 số,1 ký tự đặc biệt"
      );
      setIsLoading(false);
    } else if (
      !/^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*?&#])[A-Za-z\d@$!%*?&#]{6,}$/.test(
        newPassword
      )
    ) {
      showToastError("MK chứa ít nhất 1 chữ,1 số,1 ký tự đặc biệt");
      setIsLoading(false);
    } else if (!(newPassword === newPassword2)) {
      showToastError("Vui lòng nhập lại mật khẩu trùng khớp");
      setIsLoading(false);
    } else {
      const rs = await resetPassword(textEmail, newPassword);
      if (rs) {
        setIsLoading(false);
        setModalCreatePw(!modalCreatePw);
        setModalResetSuccess(!modalResetSuccess);
      }
    }
  };

  const handleLogin = async () => {
    setModalResetSuccess(!modalResetSuccess);
    setIsHidden(!isHidden);
    try {
      await logout();
    } catch (error) {
      console.log(error);
      showToastError("Lỗi ! Vui lòng thử lại sau");
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.toastContainer}>
        <Toast />
      </View>
      <ScrollView>
        {isHidden ? null : (
          <View
            style={{
              paddingTop: 15,
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <View style={styles.viewText}>
              <Text style={{ fontSize: 16, fontWeight: "bold" }}>
                Nhập gmail để lấy lại mật khẩu
              </Text>
            </View>
            <View style={styles.viewTextInput}>
              <TextInput
                value={textEmail}
                onChangeText={setTextEmail}
                placeholder="Nhập gmail"
                style={[styles.styleText, { width: "90%" }]}
              ></TextInput>
              <Pressable
                style={styles.styleIcon}
                onPress={() => setTextEmail(null)}
              >
                <Ionicons name="close" size={22} color="gray" />
              </Pressable>
            </View>

            <Pressable style={styles.styleButton} onPress={handleNext}>
              {isLoading ? (
                <ActivityIndicator color="white" />
              ) : (
                <Text
                  style={[
                    styles.styleText,
                    { color: "white", fontWeight: "bold" },
                  ]}
                >
                  Tiếp tục
                </Text>
              )}
            </Pressable>
          </View>
        )}
      </ScrollView>
      <View>
        {/* Modal xác nhận gmail */}
        <Modal
          animationType="slide"
          transparent={true}
          visible={modalXacNhan}
          onRequestClose={() => {
            setModalXacNhan(!modalXacNhan);
          }}
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <Text
                style={[
                  { fontWeight: "bold", textAlign: "center" },
                  styles.styleText,
                ]}
              >
                Xác nhận gmail: {textEmail} ?
              </Text>
              <Text style={{ textAlign: "center" }}>
                Gmail này sẽ được sử dụng để nhận mã xác thực
              </Text>
              <View style={{ flexDirection: "row", width: "100%" }}>
                <Pressable
                  style={[
                    styles.pressCancel,
                    { borderRightWidth: 2, borderRightColor: "#e0e3e5" },
                  ]}
                  onPress={() => {
                    setModalXacNhan(!modalXacNhan);
                    setIsHidden(!isHidden);
                  }}
                >
                  <Text style={styles.textCancel}>Huỷ</Text>
                </Pressable>
                <Pressable style={styles.pressCancel} onPress={handleXacNhan}>
                  {isLoading ? (
                    <ActivityIndicator color="black" />
                  ) : (
                    <Text style={styles.textCancel}>Xác nhận</Text>
                  )}
                </Pressable>
              </View>
            </View>
          </View>
        </Modal>
      </View>

      <Modal
        animationType="slide"
        transparent={true}
        visible={modalOTP}
        onRequestClose={() => {
          setModalOTP(!modalOTP);
        }}
      >
        <View style={styles.modalContainerPw}>
          <View style={styles.modalOTP}>
            <View style={{ backgroundColor: "#E5E7EB", padding: 10 }}>
              <Text style={{ textAlign: "center", color: "#000" }}>
                Vui lòng không chia sẻ mã xác thực để tránh mất tài khoản
              </Text>
            </View>
            <View style={{ flexDirection: "column", flex: 1 }}>
              <View
                style={{
                  flex: 1,
                  justifyContent: "center",
                  alignItems: "center",
                  padding: 10,
                }}
              >
                <FontAwesome5
                  name={"envelope"}
                  size={80}
                  color="black"
                  style={{ marginRight: 8 }}
                />
                <Text
                  style={{ fontWeight: "bold", color: "#000", marginTop: 10 }}
                >
                  Đang gửi mã xác thực đến email: {textEmail}
                </Text>
              </View>
              <View style={{ flex: 1, padding: 10 }}>
                <View style={styles.otpContainer}>
                  <OTPTextView
                    handleTextChange={handleOTPChange}
                    inputCount={6}
                    keyboardType="numeric"
                    tintColor="#00FF66"
                    offTintColor="#00FFFF"
                    containerStyle={styles.otpContainer}
                    textInputStyle={styles.otpInput}
                  />
                </View>
                <View
                  style={{
                    justifyContent: "center",
                    alignItems: "center",
                    marginBottom: 20,
                  }}
                >
                  <Pressable
                    style={{
                      flexDirection: "row",
                      width: "45%",
                      justifyContent: "space-between",
                      height: 40,
                      alignItems: "center",
                    }}
                  >
                    <Pressable
                      style={{
                        backgroundColor: isPreSendCode ? "#8a57b6" : "gray", // Đổi màu nút tùy thuộc vào giá trị của isPreSendCode
                        borderRadius: 10,
                        width: "65%",
                        height: "90%",
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                      onPress={isPreSendCode ? pressPreSendOTP : null} // Kiểm tra isPreSendCode trước khi gọi hàm pressPreSendOTP
                      disabled={!isPreSendCode} // Vô hiệu hóa nút khi isPreSendCode là false
                    >
                      <Text style={{ color: "white", fontWeight: "bold" }}>
                        Gửi lại mã
                      </Text>
                    </Pressable>
                    <Text style={{ color: "#0091FF", fontWeight: "bold" }}>
                      {timeLeft === 0 ? "0:0" : formatTime(timeLeft)}
                    </Text>
                  </Pressable>
                </View>
                <View
                  style={{
                    justifyContent: "space-evenly",
                    alignItems: "center",
                    flexDirection: "row",
                  }}
                >
                  <Pressable
                    style={{
                      backgroundColor: "#0091FF",
                      width: 120,
                      height: 50,
                      justifyContent: "center",
                      alignItems: "center",
                      borderRadius: 25,
                    }}
                    onPress={() => {
                      setModalOTP(!modalOTP);
                      setIsHidden(!isHidden);
                    }}
                  >
                    <Text style={{ color: "#fff", fontWeight: "bold" }}>
                      Huỷ
                    </Text>
                  </Pressable>
                  <Pressable
                    style={{
                      backgroundColor: "#0091FF",
                      width: 120,
                      height: 50,
                      justifyContent: "center",
                      alignItems: "center",
                      borderRadius: 25,
                    }}
                    onPress={handleVerifyOTP}
                  >
                    {isLoading ? (
                      <ActivityIndicator color="white" />
                    ) : (
                      <Text style={{ color: "#fff", fontWeight: "bold" }}>
                        Tiếp tục
                      </Text>
                    )}
                  </Pressable>
                </View>
              </View>
            </View>
          </View>
        </View>
      </Modal>

      {/* Modal Success */}
      <Modal
        animationType="slide"
        transparent={false}
        visible={modelSuccess}
        onRequestClose={() => {
          setModalSuccess(!modelSuccess);
        }}
      >
        <View style={styles.modalContainerAuthCode}>
          <View style={styles.modalSuccess}>
            <View style={{ flex: 1, justifyContent: "center" }}>
              <View style={{ alignItems: "center", paddingHorizontal: 20 }}>
                <View>
                  <Ionicons
                    name={"checkmark-circle-sharp"}
                    size={80}
                    color="#00ce3a"
                    style={{ marginRight: 8 }}
                  />
                </View>
                <Text style={styles.modalHeaderText}>
                  Đăng nhập thành công!
                </Text>
                <Text style={styles.modalText}>
                  Bây giờ bạn có thể tạo lại mật khẩu mới. Tài khoản và mật khẩu
                  này dùng để đăng nhập trên bất kỳ thiết bị nào.
                </Text>

                <View style={styles.modalButtonContainer}>
                  <Pressable
                    style={styles.buttonCreatePass}
                    onPress={() => {
                      setModalSuccess(!modelSuccess);
                      setModalCreatePw(!modalCreatePw);
                    }}
                  >
                    <Text style={styles.modalButton}>Tạo mật khẩu</Text>
                  </Pressable>
                </View>
              </View>
            </View>
          </View>
        </View>
      </Modal>

      {/* Modal Create Password */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalCreatePw}
        onRequestClose={() => {
          setModalCreatePw(!modalCreatePw);
        }}
      >
        <View style={styles.modalContainerPw}>
          <View style={styles.modalCreatePw}>
            <View
              style={{
                height: 40,
                justifyContent: "center",
                backgroundColor: "#67bed9",
                width: "50",
              }}
            >
              <Text
                style={{
                  color: "white",
                  fontSize: 16,
                  paddingHorizontal: 20,
                  paddingVertical: 10,
                  fontWeight: "bold",
                }}
              >
                Tạo mật khẩu mới
              </Text>
            </View>
            <View
              style={{
                alignItems: "center",
                paddingHorizontal: 20,
                height: "60%",
                justifyContent: "space-evenly",
              }}
            >
              <View style={styles.viewTextPwNew}>
                <Text style={[styles.styleText, styles.textBlue]}>
                  Mật khẩu mới
                </Text>
                <TouchableOpacity onPress={togglePasswordVisibility}>
                  <View>
                    <Text style={[styles.styleText, styles.textGray]}>
                      {buttonText}
                    </Text>
                  </View>
                </TouchableOpacity>
              </View>
              <View style={styles.styleViewGray}>
                <TextInput
                  style={[styles.styleText, styles.textInput, { width: "90%" }]}
                  value={newPassword}
                  onChangeText={setNewPassword}
                  placeholder="Nhập mật khẩu mới"
                  placeholderTextColor="gray"
                  secureTextEntry={!showPassword}
                ></TextInput>
                {newPassword ? (
                  <Pressable
                    style={styles.styleIcon}
                    onPress={() => setNewPassword(null)}
                  >
                    <Ionicons name="close" size={22} color="gray" />
                  </Pressable>
                ) : null}
              </View>
              <View style={styles.styleViewGray}>
                <TextInput
                  style={[styles.styleText, styles.textInput, { width: "90%" }]}
                  value={newPassword2}
                  onChangeText={setNewPassword2}
                  placeholder="Nhập lại mật khẩu mới"
                  placeholderTextColor="gray"
                  secureTextEntry={!showPassword}
                ></TextInput>
                {newPassword2 ? (
                  <Pressable
                    style={styles.styleIcon}
                    onPress={() => setNewPassword2(null)}
                  >
                    <Ionicons name="close" size={22} color="gray" />
                  </Pressable>
                ) : null}
              </View>
              <Pressable
                style={[styles.styleButton, styles.styleCenter, { margin: 30 }]}
                onPress={handleResetPassword}
              >
                {isLoading ? (
                  <ActivityIndicator color="white" />
                ) : (
                  <Text
                    style={[
                      styles.styleText,
                      { color: "white", fontWeight: "bold" },
                    ]}
                  >
                    Cập nhật
                  </Text>
                )}
              </Pressable>
            </View>
          </View>
        </View>
      </Modal>

      {/* Modal reset thành công*/}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalResetSuccess}
        onRequestClose={() => {
          setModalResetSuccess(!modalResetSuccess);
        }}
      >
        <View style={styles.modalContainerAuthCode}>
          <View style={styles.modalSuccess}>
            <View style={{ flex: 1, justifyContent: "center" }}>
              <View style={{ alignItems: "center", paddingHorizontal: 20 }}>
                <View>
                  <Ionicons
                    name={"checkmark-circle-sharp"}
                    size={80}
                    color="#00ce3a"
                    style={{ marginRight: 8 }}
                  />
                </View>
                <Text style={styles.modalHeaderText}>
                  Đổi mật khẩu thành công!
                </Text>
                <Text style={styles.modalText}>
                  Bây giờ bạn có thể quay lại trang đăng nhập để nhập mật khẩu
                  mới.
                </Text>

                <View style={styles.modalButtonContainer}>
                  {isLoading ? (
                    <ActivityIndicator color="white" />
                  ) : (
                    <Pressable
                      style={[styles.buttonCreatePass, { margin: 20 }]}
                      onPress={handleLogin}
                    >
                      <Text style={styles.modalButton}>Trở về</Text>
                    </Pressable>
                  )}
                </View>
              </View>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};
export default ForgotPassword;

