import React, { useEffect, useMemo, useState } from "react";
import { View, Text, Image, Pressable, ScrollView, Modal } from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";
import { useAuthContext } from "../../contexts/AuthContext";
import { format } from "date-fns";
import RadioButton from "react-native-radio-buttons-group";

const PersonalInfo = ({ navigation }) => {
  const { authUser } = useAuthContext();
  const gender = authUser?.profile?.gender === "male" ? "Nam" : "Nữ";

  const dob = authUser?.profile?.dob;
  let dobFormat = format(new Date(dob), "dd/MM/yyyy");

  useEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <View style={{ flexDirection: "row", paddingHorizontal: 5 }}>
          <Pressable style={{ paddingHorizontal: 8 }}>
            <Ionicons name="sync-circle-outline" size={24} color="white" />
          </Pressable>
          <Pressable
            onPress={() => {
              navigation.navigate("PersonalDetail");
            }}
            style={{ paddingHorizontal: 8 }}
          >
            <Ionicons
              name="ellipsis-horizontal-sharp"
              size={24}
              color="white"
            />
          </Pressable>
        </View>
      ),
      headerTitle: () => (
        <Text style={{ color: "white", fontSize: 20, fontWeight: "bold" }}>
          Thông tin cá nhân
        </Text>
      ),
      headerTintColor: "#fff",
      headerStyle: {
        backgroundColor: "#0091FF",
        shadowColor: "#fff",
      },
    });
  }, [navigation]);

  const [modalVisible, setModalVisible] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [selectedId, setSelectedId] = useState();

  // Radio buttons configuration
  const radioButtons = useMemo(
    () => [
      {
        id: "1",
        label: "Nam",
        value: "option1",
        color: "#0091FF",
        size: 24,
      },
      {
        id: "2",
        label: "Nữ",
        value: "option2",
        color: "#0091FF",
        size: 24,
      },
    ],
    []
  );

  return (
    <View style={{ backgroundColor: "#EFEFEF", flex: 1 }}>
      <ScrollView>
        <View>
          <Image
            source={{
              uri: authUser?.profile?.background?.url,
            }}
            style={{ width: "100%", height: 250 }}
          />
          <View
            style={{
              display: "flex",
              flexDirection: "row",
              marginTop: -100,
              alignItems: "left",
              marginBot: 100,
              paddingBottom: 10,
              paddingLeft: 10,
            }}
          >
            <Pressable onPress={() => setModalVisible(true)}>
              <Image
                source={{
                  uri: authUser?.profile?.avatar?.url,
                }}
                style={{ width: 75, height: 75, borderRadius: 48 }}
              />
            </Pressable>
            <View
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                paddingLeft: 20,
              }}
            >
              <Text style={{ fontSize: 18, fontWeight: "bold", color: "#fff" }}>
                {authUser?.profile?.name}
              </Text>
            </View>
          </View>
          <View
            style={{ backgroundColor: "#fff", paddingTop: 15, paddingLeft: 10 }}
          >
            <Text
              style={{ fontSize: 18, fontWeight: "500", paddingBottom: 10 }}
            >
              Thông tin cá nhân
            </Text>
            <View>
              <View style={styles.infoRow}>
                <Text style={styles.infoLabel}>Giới tính</Text>
                <Text style={styles.infoText}>{gender}</Text>
              </View>
              <View style={styles.infoRow}>
                <Text style={styles.infoLabel}>Ngày sinh</Text>
                <Text style={styles.infoText}>{dobFormat}</Text>
              </View>
              <View style={styles.infoRow}>
                <Text style={styles.infoLabel}>Điện thoại</Text>
                <View style={styles.phoneContainer}>
                  <Text style={styles.infoText}>{authUser?.phone}</Text>
                  <Text style={styles.infoHint}>
                    Số điện thoại này chỉ hiển thị với người có lưu số bạn trong
                    danh bạ máy
                  </Text>
                </View>
              </View>
            </View>
          </View>
        </View>
      </ScrollView>

      {/* Edit Modal */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            {/* Your modal content here */}
          </View>
        </View>
      </Modal>

      {/* Update Modal */}
      <Modal
        animationType="slide"
        transparent={false}
        visible={showModal}
        onRequestClose={() => setShowModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
            <Pressable
              onPress={() => setShowModal(!showModal)}
              style={styles.backButton}
            >
              <Ionicons name="arrow-back" size={34} color="white" />
            </Pressable>
            <Text style={styles.modalTitle}>Chỉnh sửa thông tin</Text>
          </View>
          <View style={styles.modalBody}>{/* Your modal content here */}</View>
        </View>
      </Modal>
    </View>
  );
};

const styles = {
  infoRow: {
    height: 50,
    paddingTop: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#bbb",
    flexDirection: "row",
  },
  infoLabel: {
    width: "30%",
    fontSize: 16,
    fontWeight: "400",
  },
  infoText: {
    fontSize: 16,
    fontWeight: "400",
  },
  phoneContainer: {
    width: "67%",
    flexDirection: "column",
  },
  infoHint: {
    fontSize: 14,
    fontWeight: "400",
    color: "gray",
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0,0,0,0.5)",
  },
  modalContent: {
    backgroundColor: "white",
    padding: 20,
    width: "100%",
    height: 300,
  },
  modalHeader: {
    flexDirection: "row",
    backgroundColor: "#0091FF",
    width: "100%",
    height: "12%",
    alignItems: "flex-end",
  },
  backButton: {
    paddingHorizontal: 15,
  },
  modalTitle: {
    color: "white",
    marginBottom: 15,
    fontSize: 20,
    fontWeight: "bold",
  },
  modalBody: {
    display: "flex",
    padding: 20,
    flexDirection: "row",
    height: "25%",
  },
};

export default PersonalInfo;
