import axios from "axios";
import config from "./config";
import { Platform } from "react-native"; // Import Platform từ 'react-native'
import AsyncStorage from "@react-native-async-storage/async-storage"; // Import AsyncStorage từ '@react-native-async-storage/async-storage' cho React Native

import toast from "react-hot-toast"; // Đảm bảo cài đặt và import toast từ thư viện react-hot-toast

const axiosInstance = axios.create({
  baseURL: config.baseURL,
});

axiosInstance.interceptors.request.use(
  async (config) => {
    if (Platform.OS === "web") {
      // Kiểm tra nếu đang chạy trên Web
      // Thực hiện các xử lý cho môi trường Web
    } else if (Platform.OS === "android" || Platform.OS === "ios") {
      // Kiểm tra nếu đang chạy trên React Native
      try {
        const token = await AsyncStorage.getItem("accessToken"); // Sử dụng AsyncStorage cho React Native
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
      } catch (error) {
        return Promise.reject(error);
      }
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

axiosInstance.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    if (Platform.OS === "web") {
      // Kiểm tra nếu đang chạy trên Web
      // Thực hiện các xử lý cho môi trường Web
    } else if (Platform.OS === "android" || Platform.OS === "ios") {
      // Kiểm tra nếu đang chạy trên React Native
      const originalRequest = error.config;

      if (error.response.status === 401) {
        try {
          const refreshToken = await AsyncStorage.getItem("refreshToken"); // Sử dụng AsyncStorage cho React Native
          if (!refreshToken) {
            toast.error("Your session has expired. Please login again.");
            throw new Error("No refresh token available.");
          }

          const refreshedTokenResponse = await axiosInstance.post(
            "/auth/refreshToken",
            {
              refreshToken: refreshToken,
            }
          );
          const newAccessToken = refreshedTokenResponse.data.newAccessToken;
          await AsyncStorage.setItem("accessToken", newAccessToken); // Sử dụng AsyncStorage cho React Native

          originalRequest.headers.Authorization = `Bearer ${newAccessToken}`;
          return axiosInstance(originalRequest);
        } catch (refreshError) {
          console.error("Refresh token failed:", refreshError);
          toast.error("Your session has expired. Please login again.");
          await AsyncStorage.clear(); // Sử dụng AsyncStorage cho React Native
          window.location.href = "/login"; // Chuyển hướng người dùng đến trang đăng nhập (Chỉ áp dụng cho Web, bạn cần thay đổi tương ứng cho React Native)
          return Promise.reject(refreshError);
        }
      }
    }
    return Promise.reject(error);
  }
);

export default axiosInstance;
